import React, { useState } from 'react';

const Homepage = () => {
    const [verse, setVerse] = useState(0);
    const [chapter, setChapter] = useState(0);
    const [quote, setQuote] = useState('');
    const create = () => {
        fetch('https://bible-api.com/data/web/random')
            .then(response => response.json()) 
            .then(data => {
                console.log(data.random_verse.text, "new one", data.random_verse.book, data.random_verse.chapter, data.random_verse.verse);

                // Return the data for the next .then()
                return data;
            })
            .then(data => setQuote(data.random_verse?.text || 'No quote found'))
            .catch(error => console.error(error));  
    };
    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Welcome to Bible Quotes</h1>
            <span> {chapter}</span>
            <span>{verse}</span>
            <span> {quote} </span>
            <div>
                <button onClick={create}> generate bible quote </button>
            </div>
        </div>
    );
};

export default Homepage;
